package angie.basic.day02;

public class Calculation01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double number = (1 / 3) * 3;
		int quotient = 7 / 3;
		int remainder = 7 % 3;
		int result = 11;
		result /= 2;

		// 결과 처리
		System.out.println(number);// 결과 처리
		System.out.println(quotient);// 결과 처리
		System.out.println(remainder);// 결과 처리
		System.out.println(result);// 결과 처리
	}

}
